// FirebaseTest.js - Create this file in your src folder for testing
import React, { useState } from 'react';
import { initializeApp } from 'firebase/app';
import { getFirestore, collection, addDoc, serverTimestamp } from 'firebase/firestore';

const FirebaseTest = () => {
  const [status, setStatus] = useState('Ready to test');
  const [error, setError] = useState(null);

  // Your exact Firebase config
  const firebaseConfig = {
    apiKey: "AIzaSyBERr5NV_1TszM1VwxUAIEA57LWgcG21pc",
    authDomain: "nevloh-website.firebaseapp.com",
    projectId: "nevloh-website",
    storageBucket: "nevloh-website.firebasestorage.app",
    messagingSenderId: "984849142454",
    appId: "1:984849142454:web:f60c2a9eca53d42f3fcc17"
  };

  const testFirebase = async () => {
    setStatus('Testing...');
    setError(null);

    try {
      console.log('üî• Initializing Firebase...');
      const app = initializeApp(firebaseConfig);

      console.log('üî• Getting Firestore...');
      const db = getFirestore(app);

      console.log('üî• Creating test document...');
      const testData = {
        message: 'Test from contact form debugging',
        timestamp: serverTimestamp(),
        testId: Date.now()
      };

      const docRef = await addDoc(collection(db, 'test_collection'), testData);

      console.log('‚úÖ Document written with ID: ', docRef.id);
      setStatus(`‚úÖ Success! Document ID: ${docRef.id}`);

    } catch (error) {
      console.error('‚ùå Error details:', error);
      setError(error.message);
      setStatus('‚ùå Failed');
    }
  };

  return (
    <div className="p-8 max-w-md mx-auto bg-white rounded-lg shadow-lg">
      <h2 className="text-2xl font-bold mb-4">Firebase Connection Test</h2>

      <div className="mb-4">
        <p className="font-semibold">Status:</p>
        <p className={`p-2 rounded ${
          status.includes('‚úÖ') ? 'bg-green-100 text-green-800' : 
          status.includes('‚ùå') ? 'bg-red-100 text-red-800' : 
          'bg-blue-100 text-blue-800'
        }`}>
          {status}
        </p>
      </div>

      {error && (
        <div className="mb-4">
          <p className="font-semibold">Error:</p>
          <p className="p-2 bg-red-100 text-red-800 rounded text-sm">
            {error}
          </p>
        </div>
      )}

      <button
        onClick={testFirebase}
        className="w-full bg-blue-600 text-white py-2 px-4 rounded hover:bg-blue-700"
      >
        Test Firebase Connection
      </button>

      <div className="mt-4 text-xs text-gray-600">
        <p><strong>Project ID:</strong> {firebaseConfig.projectId}</p>
        <p><strong>Auth Domain:</strong> {firebaseConfig.authDomain}</p>
      </div>
    </div>
  );
};

export default FirebaseTest;
