// firebase.js
import { initializeApp } from 'firebase/app';
import { getFirestore } from 'firebase/firestore';
import { getAuth } from 'firebase/auth';

// Your Firebase configuration
// Replace with your actual config from Firebase Console
const firebaseConfig = {
  apiKey: process.env.REACT_APP_FIREBASE_API_KEY || "AIzaSyBERr5NV_1TszM1VwxUAIEA57LWgcG21pc",
  authDomain: process.env.REACT_APP_FIREBASE_AUTH_DOMAIN || "nevloh-website.firebaseapp.com",
  projectId: process.env.REACT_APP_FIREBASE_PROJECT_ID || "nevloh-website",
  storageBucket: process.env.REACT_APP_FIREBASE_STORAGE_BUCKET || "nevloh-website.firebasestorage.app",
  messagingSenderId: process.env.REACT_APP_FIREBASE_MESSAGING_SENDER_ID || "984849142454",
  appId: process.env.REACT_APP_FIREBASE_APP_ID || "1:984849142454:web:f60c2a9eca53d42f3fcc17"
};

// Initialize Firebase
const app = initializeApp(firebaseConfig);

// Initialize Firestore
export const db = getFirestore(app);

// Initialize Auth (for future use)
export const auth = getAuth(app);

// Export the app
export default app;
