// pages/LoginPage.js
import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import {
  Mail,
  Lock,
  Eye,
  EyeOff,
  Shield,
  Users,
  Truck,
  ArrowRight,
  CheckCircle,
  Fuel,
  Settings,
  AlertCircle,
  Home
} from 'lucide-react';

// Demo credentials for testing
const DEMO_CREDENTIALS = {
  admin: {
    email: 'admin@nevloh.com',
    password: 'admin123',
    role: 'admin'
  },
  customer: {
    email: 'customer@nevloh.com',
    password: 'customer123',
    role: 'customer'
  },
  driver: {
    email: 'driver@nevloh.com',
    password: 'driver123',
    role: 'driver'
  }
};

const LoginPage = () => {
  const navigate = useNavigate();
  const [formData, setFormData] = useState({
    email: '',
    password: '',
    role: 'admin' // Default to admin for easy access to CustomerDashboard
  });
  const [showPassword, setShowPassword] = useState(false);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState('');

  // Check if user is already authenticated
  useEffect(() => {
    const isAuthenticated = localStorage.getItem('isAuthenticated') === 'true';
    const userRole = localStorage.getItem('userRole');

    if (isAuthenticated && userRole) {
      // Redirect to appropriate dashboard
      const dashboardRoutes = {
        customer: '/customer-portal',
        driver: '/driver-dashboard',
        admin: '/admin-dashboard'
      };
      navigate(dashboardRoutes[userRole] || '/');
    }
  }, [navigate]);

  const roles = [
    {
      id: 'admin',
      name: 'Admin',
      icon: Shield,
      description: 'Access customer management and analytics',
      color: 'purple',
      dashboardPath: '/admin-dashboard'
    },
    {
      id: 'customer',
      name: 'Customer',
      icon: Users,
      description: 'Access your fuel orders and delivery tracking',
      color: 'blue',
      dashboardPath: '/customer-portal'
    },
    {
      id: 'driver',
      name: 'Driver',
      icon: Truck,
      description: 'Manage deliveries and route assignments',
      color: 'green',
      dashboardPath: '/driver-dashboard'
    }
  ];

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
    setError(''); // Clear error when user types
  };

  const handleRoleSelect = (roleId) => {
    setFormData(prev => ({
      ...prev,
      role: roleId,
      // Auto-fill credentials for demo
      email: DEMO_CREDENTIALS[roleId].email,
      password: DEMO_CREDENTIALS[roleId].password
    }));
    setError('');
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsLoading(true);
    setError('');

    try {
      // Simulate authentication process
      await new Promise(resolve => setTimeout(resolve, 1500));

      // Check credentials
      const selectedRoleCredentials = DEMO_CREDENTIALS[formData.role];

      if (formData.email === selectedRoleCredentials.email &&
          formData.password === selectedRoleCredentials.password) {

        // Store authentication in localStorage
        localStorage.setItem('isAuthenticated', 'true');
        localStorage.setItem('userRole', formData.role);
        localStorage.setItem('userEmail', formData.email);
        localStorage.setItem('loginTime', new Date().toISOString());

        // Get the dashboard path for the selected role
        const selectedRole = roles.find(role => role.id === formData.role);
        const dashboardPath = selectedRole.dashboardPath;

        console.log(`Login successful! Redirecting to ${dashboardPath}`);
        navigate(dashboardPath);

      } else {
        setError('Invalid email or password. Please try the demo credentials.');
      }

    } catch (error) {
      setError('Login failed. Please try again.');
      console.error('Login error:', error);
    } finally {
      setIsLoading(false);
    }
  };

  const handleQuickLogin = (roleId) => {
    const credentials = DEMO_CREDENTIALS[roleId];
    localStorage.setItem('isAuthenticated', 'true');
    localStorage.setItem('userRole', roleId);
    localStorage.setItem('userEmail', credentials.email);
    localStorage.setItem('loginTime', new Date().toISOString());

    const selectedRole = roles.find(role => role.id === roleId);
    navigate(selectedRole.dashboardPath);
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-50 to-blue-100 flex items-center justify-center py-12 px-4">
      <div className="max-w-md w-full">
        {/* Header */}
        <div className="text-center mb-8">
          <div className="w-16 h-16 bg-blue-600 rounded-2xl flex items-center justify-center mx-auto mb-4">
            <Fuel size={32} className="text-white" />
          </div>
          <h1 className="text-3xl font-bold text-gray-800 mb-2">Welcome Back</h1>
          <p className="text-gray-600">Sign in to your Nevloh Limited account</p>

          {/* Back to Home Link */}
          <Link
            to="/"
            className="inline-flex items-center text-sm text-blue-600 hover:text-blue-500 mt-2"
          >
            <Home size={16} className="mr-1" />
            Back to Website
          </Link>
        </div>

        {/* Demo Credentials Info */}
        <div className="bg-gradient-to-r from-blue-600 to-purple-600 rounded-xl p-4 mb-6 text-white">
          <div className="flex items-center justify-center mb-2">
            <Settings size={20} className="mr-2" />
            <span className="font-semibold">Demo Login Credentials</span>
          </div>
          <div className="text-sm space-y-1">
            <p><strong>Admin:</strong> admin@nevloh.com / admin123</p>
            <p><strong>Customer:</strong> customer@nevloh.com / customer123</p>
            <p><strong>Driver:</strong> driver@nevloh.com / driver123</p>
          </div>
        </div>

        {/* Role Selection */}
        <div className="mb-6">
          <label className="block text-sm font-medium text-gray-700 mb-3">
            Select Your Role
          </label>
          <div className="grid grid-cols-1 gap-3">
            {roles.map((role) => {
              const IconComponent = role.icon;
              const isSelected = formData.role === role.id;

              return (
                <button
                  key={role.id}
                  type="button"
                  onClick={() => handleRoleSelect(role.id)}
                  className={`flex items-center p-4 rounded-xl border-2 transition-all duration-300 ${
                    isSelected
                      ? `border-${role.color}-500 bg-${role.color}-50`
                      : 'border-gray-200 bg-white hover:border-gray-300'
                  }`}
                >
                  <div className={`w-10 h-10 rounded-lg flex items-center justify-center mr-3 ${
                    isSelected ? `bg-${role.color}-100` : 'bg-gray-100'
                  }`}>
                    <IconComponent size={20} className={isSelected ? `text-${role.color}-600` : 'text-gray-600'} />
                  </div>
                  <div className="text-left flex-1">
                    <div className="font-semibold text-gray-800">{role.name}</div>
                    <div className="text-xs text-gray-600">{role.description}</div>
                  </div>
                  {isSelected && (
                    <CheckCircle size={20} className={`text-${role.color}-600`} />
                  )}
                </button>
              );
            })}
          </div>
        </div>

        {/* Login Form */}
        <form onSubmit={handleSubmit} className="bg-white rounded-2xl p-6 shadow-lg">
          {/* Error Message */}
          {error && (
            <div className="bg-red-50 border border-red-200 rounded-lg p-3 mb-4">
              <div className="flex items-center">
                <AlertCircle size={18} className="text-red-600 mr-2" />
                <p className="text-red-700 text-sm">{error}</p>
              </div>
            </div>
          )}

          <div className="space-y-4">
            {/* Email */}
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">
                Email Address
              </label>
              <div className="relative">
                <Mail size={20} className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400" />
                <input
                  type="email"
                  name="email"
                  value={formData.email}
                  onChange={handleInputChange}
                  className="w-full pl-10 pr-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                  placeholder="Enter your email"
                  required
                />
              </div>
            </div>

            {/* Password */}
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">
                Password
              </label>
              <div className="relative">
                <Lock size={20} className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400" />
                <input
                  type={showPassword ? 'text' : 'password'}
                  name="password"
                  value={formData.password}
                  onChange={handleInputChange}
                  className="w-full pl-10 pr-12 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                  placeholder="Enter your password"
                  required
                />
                <button
                  type="button"
                  onClick={() => setShowPassword(!showPassword)}
                  className="absolute right-3 top-1/2 transform -translate-y-1/2 text-gray-400 hover:text-gray-600"
                >
                  {showPassword ? <EyeOff size={20} /> : <Eye size={20} />}
                </button>
              </div>
            </div>

            {/* Remember Me & Forgot Password */}
            <div className="flex items-center justify-between">
              <label className="flex items-center">
                <input
                  type="checkbox"
                  className="rounded border-gray-300 text-blue-600 focus:ring-blue-500"
                />
                <span className="ml-2 text-sm text-gray-600">Remember me</span>
              </label>
              <Link to="/forgot-password" className="text-sm text-blue-600 hover:text-blue-500">
                Forgot password?
              </Link>
            </div>

            {/* Submit Button */}
            <button
              type="submit"
              disabled={isLoading}
              className={`w-full py-3 px-4 rounded-lg font-semibold transition-all duration-300 ${
                isLoading
                  ? 'bg-gray-400 cursor-not-allowed'
                  : 'bg-blue-600 hover:bg-blue-700 transform hover:scale-105'
              } text-white`}
            >
              {isLoading ? (
                <div className="flex items-center justify-center">
                  <div className="animate-spin rounded-full h-5 w-5 border-b-2 border-white mr-2"></div>
                  Signing In...
                </div>
              ) : (
                <div className="flex items-center justify-center">
                  <span>Sign In</span>
                  <ArrowRight size={20} className="ml-2" />
                </div>
              )}
            </button>
          </div>
        </form>

        {/* Quick Access Buttons */}
        <div className="mt-6 space-y-2">
          <p className="text-center text-sm text-gray-600 mb-3">Quick Access (Demo):</p>

          <button
            onClick={() => handleQuickLogin('admin')}
            className="w-full bg-purple-600 text-white py-3 px-4 rounded-lg font-semibold hover:bg-purple-700 transition-colors flex items-center justify-center"
          >
            <Shield size={18} className="mr-2" />
            Login as Admin (Customer Dashboard)
          </button>

          <div className="grid grid-cols-2 gap-2">
            <button
              onClick={() => handleQuickLogin('customer')}
              className="bg-blue-600 text-white py-2 px-3 rounded-lg text-sm hover:bg-blue-700 transition-colors flex items-center justify-center"
            >
              <Users size={16} className="mr-1" />
              Customer
            </button>
            <button
              onClick={() => handleQuickLogin('driver')}
              className="bg-green-600 text-white py-2 px-3 rounded-lg text-sm hover:bg-green-700 transition-colors flex items-center justify-center"
            >
              <Truck size={16} className="mr-1" />
              Driver
            </button>
          </div>
        </div>

        {/* Sign Up Link */}
        <div className="text-center mt-6">
          <p className="text-gray-600">
            Don't have an account?{' '}
            <Link to="/signup" className="text-blue-600 hover:text-blue-500 font-semibold">
              Sign up here
            </Link>
          </p>
        </div>
      </div>
    </div>
  );
};

export default LoginPage;
