// FirebaseTest.js - Create this file to test your connection
import React, { useEffect, useState } from 'react';
import { db } from './firebase';
import { collection, addDoc, getDocs } from 'firebase/firestore';

const FirebaseTest = () => {
  const [status, setStatus] = useState('Testing...');
  const [config, setConfig] = useState({});

  useEffect(() => {
    testFirebase();
  }, []);

  const testFirebase = async () => {
    try {
      // Check if environment variables are loaded
      const firebaseConfig = {
        apiKey: process.env.REACT_APP_FIREBASE_API_KEY,
        authDomain: process.env.REACT_APP_FIREBASE_AUTH_DOMAIN,
        projectId: process.env.REACT_APP_FIREBASE_PROJECT_ID,
        storageBucket: process.env.REACT_APP_FIREBASE_STORAGE_BUCKET,
        messagingSenderId: process.env.REACT_APP_FIREBASE_MESSAGING_SENDER_ID,
        appId: process.env.REACT_APP_FIREBASE_APP_ID
      };

      setConfig(firebaseConfig);

      // Test Firestore connection
      const testCollection = collection(db, 'test');

      // Try to read from Firestore
      await getDocs(testCollection);

      setStatus('✅ Firebase connection successful!');
      console.log('Firebase configuration:', firebaseConfig);

    } catch (error) {
      setStatus('❌ Firebase connection failed: ' + error.message);
      console.error('Firebase error:', error);
    }
  };

  return (
    <div className="p-6 bg-white rounded-lg shadow-lg max-w-2xl mx-auto mt-8">
      <h2 className="text-2xl font-bold mb-4">Firebase Connection Test</h2>

      <div className="mb-4">
        <h3 className="font-semibold mb-2">Status:</h3>
        <p className={`p-2 rounded ${status.includes('✅') ? 'bg-green-100 text-green-800' : status.includes('❌') ? 'bg-red-100 text-red-800' : 'bg-yellow-100 text-yellow-800'}`}>
          {status}
        </p>
      </div>

      <div className="mb-4">
        <h3 className="font-semibold mb-2">Configuration Check:</h3>
        <div className="bg-gray-100 p-3 rounded text-sm">
          <p><strong>API Key:</strong> {config.apiKey ? '✅ Loaded' : '❌ Missing'}</p>
          <p><strong>Project ID:</strong> {config.projectId || '❌ Missing'}</p>
          <p><strong>Auth Domain:</strong> {config.authDomain || '❌ Missing'}</p>
          <p><strong>Storage Bucket:</strong> {config.storageBucket || '❌ Missing'}</p>
        </div>
      </div>

      <button
        onClick={testFirebase}
        className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700"
      >
        Test Again
      </button>
    </div>
  );
};

export default FirebaseTest;
